pragma solidity ^0.8.18;
import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155SupplyUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/StringsUpgradeable.sol";
import "./storage/storage.sol";

contract GenZNFT is
    ERC1155Upgradeable,
    OwnableUpgradeable,
    MazeStorage,
    PausableUpgradeable,
    ERC1155SupplyUpgradeable
{
    using StringsUpgradeable for uint256;

    function initialize(
        string memory _name,
        string memory _uri
    ) public initializer {
        __ERC1155_init(_uri);
        __Ownable_init();
        __Pausable_init();
        __ERC1155Supply_init();
        name = _name;
    }

    // Reject incoming ether
    receive() external payable {
        revert();
    }

    /************************************************
     * MODIFIERS
     ***********************************************/
    modifier IdExists(uint256 _id) {
        if (exists(_id) == false) revert IdNotExists();
        _;
    }
    /************************************************
     * COUSTOM ERRORS
     ***********************************************/

    error ZeroAmount();
    error IdNotExists();
    error SupplyCapReached();
    error NewCapCanNotZero();
    error EmptyUri();
    error CanNotDecreaseNFTNum();

    /************************************************
     * EVENTS
     ***********************************************/
    event UpdateNFTNum(uint256 indexed oldNum, uint256 indexed newNum);
    event Mint(
        address indexed account,
        uint256 indexed id,
        uint256 indexed amount
    );
    event UpdateCap(uint256 oldCap, uint256 newCap);

    /************************************************
     * ONLY OWNER FUNCTIONS
     ***********************************************/
    /**
     * @dev Set the URI for the NFTs
     * @param newUri The new base URI for the NFTs
     */
    function setURI(string memory newUri) external onlyOwner {
        if (bytes(newUri).length == 0) revert EmptyUri();
        _setURI(newUri);
    }

    /**
     * @dev Mint new NFTs
     * @param id The NFT type ID
     * @param amount The number of NFTs to mint
     */
    function mint(uint256 id, uint256 amount) external  onlyOwner {
        if (amount == 0) revert ZeroAmount();
        if (id > numOfNFT) revert IdNotExists();
        if (totalSupply(id) + amount > supplyCap[id]) revert SupplyCapReached();
        _mint(msg.sender, id, amount, "");
        emit Mint(msg.sender, id, amount);
    }

    /**
     * @dev Update the supply cap for an NFT
     * @param id The NFT type ID
     * @param newCap The new supply cap for the NFT type
     */
    function updateSupplyCap(
        uint256 id,
        uint256 newCap
    ) external IdExists(id) onlyOwner {
        if (newCap == 0) revert NewCapCanNotZero();
        emit UpdateCap(supplyCap[id], newCap);
        supplyCap[id] = newCap;
    }

    /**
     * @dev Update the total number of NFTs available
     * @param newNumber The new total number of NFTs
     */
    function updateNFTNumber(uint256 newNumber) external onlyOwner {
        if (newNumber < numOfNFT) revert CanNotDecreaseNFTNum();
        emit UpdateNFTNum(numOfNFT, newNumber);
        numOfNFT = newNumber;
    }

    /**
     * @dev Before transferring tokens, check if the contract is paused
     */
    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    )
        internal
        override(ERC1155Upgradeable, ERC1155SupplyUpgradeable)
        whenNotPaused
    {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    /************************************************
     * PUBLIC  FUNCTIONS
     ***********************************************/
    /**
     * @dev Get the URI for a specific NFT
     * @param _id The NFT type ID
     * @return The URI of the NFT
     */
    function uri(
        uint256 _id
    ) public view virtual override IdExists(_id) returns (string memory) {
        return
            string(abi.encodePacked(super.uri(_id), _id.toString(), ".json"));
    }
}
